mapper.update:
UPDATE B SET SANCTN_ID = (CASE WHEN B.SANCTN_ID IS NULL THEN #{rgstId} ELSE B.SANCTN_ID END) ,ISSU_DE = GETDATE() FROM CRTF_ISSU B WHERE REQST_NO = #{reqstNo}
UPDATE A SET RECPT_MTH = (CASE WHEN A.RECPT_MTH = NULL THEN #{recptMth} ELSE RECPT_MTH END)
,SETLE_MTH = (CASE WHEN A.SETLE_MTH = NULL THEN #{setleMth} ELSE SETLE_MTH END)
FROM CRTF_REQST A WHERE REQST_NO = #{reqstNo}
UPDATE A SET fxnum = #{fxnum} FROM CRTF_REQST A WHERE REQST_NO = #{reqstNo}

mapper.sql:
WHERE DELETE_AT = 'N'

	AND (
		APPLCNT_IHIDNUM LIKE CONCAT(#{jumin1} , '%')
		OR ISSU_TRGTER_IHIDNUM LIKE CONCAT(#{jumin1} , '%')
	)


	AND (
		APPLCNT_IHIDNUM LIKE CONCAT('%', #{jumin2})
		OR ISSU_TRGTER_IHIDNUM LIKE CONCAT('%', #{jumin2})
	)


	AND APPLCNT_NM LIKE CONCAT('%', #{applcntNm}, '%')


	AND ISSU_TRGTER_NM LIKE CONCAT('%', #{issuTrgterNm}, '%')


	AND CONVERT(VARCHAR(10), RQSTDT, 23) >= #{bgnRqstdt}


	AND CONVERT(VARCHAR(10), RQSTDT, 23) <= #{endRqstdt}


	AND CONVERT(VARCHAR(10), ISSU_ON, 23) >= #{bgnIssuOn}


	AND CONVERT(VARCHAR(10), ISSU_ON, 23) <= #{endIssuOn}


	AND SETLE_MTH = #{setleMth}


	AND A.REQST_NO = #{reqstNo}


	AND A.REQST_NO IN (SELECT DISTINCT REQST_NO FROM CRTF_ISSU WHERE ISSU_DE IS NULL)


	AND A.REQST_NO IN (SELECT DISTINCT REQST_NO FROM CRTF_ISSU WHERE ISSU_DE IS NOT NULL)


	AND A.CHRG_BRFFC = #{chrgBrffc}

mapper.select:
SELECT COUNT(*) FROM( SELECT A.REQST_NO, (SELECT TOP 1 PROGRS_STTUS FROM FEE_CRTF_PROGRS WHERE REQST_NO = A.REQST_NO ORDER BY SN DESC) SANCTN_STTUS, -- 수수료감면 신청 결재상태 (SELECT TOP 1 ISNULL(CONVERT(VARCHAR(10), ISSU_DE , 23), '') FROM CRTF_ISSU WHERE REQST_NO = A.REQST_NO) ISSU_DE FROM CRTF_REQST A LEFT JOIN epdbs_link.epdbs.dbo.공제회 B1 ON A.CHRG_BRFFC = B1.인덱스 LEFT JOIN CMMN_CD C ON C.CD_ID = A.SPT_SE LEFT JOIN CMMN_CD D ON D.CD_ID = A.RECPT_MTH LEFT JOIN CMMN_CD E ON E.CD_ID = A.SETLE_MTH
) A WHERE 1=1
AND A.FEE_RDCXPT_REQST != 'Y'
AND A.SANCTN_STTUS = #{feeRdcxptStat}

SELECT * FROM( SELECT ROW_NUMBER() OVER(ORDER BY A.REQST_NO DESC) AS ROWNUM , * FROM( SELECT C.CD_NM SPT_SE_NM, A.REQST_NO, APPLCNT_NM, CONVERT(VARCHAR(10), RQSTDT, 23) RQSTDT, CONVERT(VARCHAR(10), ISSU_ON, 23) ISSU_ON, ISSU_TRGTER_IHIDNUM, ISSU_TRGTER_NM, RECPT_MTH, D.CD_NM RECPT_MTH_NM, SETLE_MTH, E.CD_NM SETLE_MTH_NM, JSSFC_NO, ISSU_CO, A.RGST_ID, DETAIL_HIST_INCLS_AT, IHIDNUM_OTHBC_AT, FEE_RDCXPT_REQST, B1.명칭 CHRG_BRFFC, (SELECT TOP 1 PROGRS_STTUS FROM FEE_CRTF_PROGRS WHERE REQST_NO = A.REQST_NO ORDER BY SN DESC) SANCTN_STTUS, -- 수수료감면 신청 결재상태 (SELECT TOP 1 ISNULL(CONVERT(VARCHAR(10), ISSU_DE , 23), '') FROM CRTF_ISSU WHERE REQST_NO = A.REQST_NO) ISSU_DE FROM CRTF_REQST A LEFT JOIN epdbs_link.epdbs.dbo.공제회 B1 ON A.CHRG_BRFFC = B1.인덱스 LEFT JOIN CMMN_CD C ON C.CD_ID = A.SPT_SE LEFT JOIN CMMN_CD D ON D.CD_ID = A.RECPT_MTH LEFT JOIN CMMN_CD E ON E.CD_ID = A.SETLE_MTH
) A WHERE 1=1
AND A.FEE_RDCXPT_REQST != 'Y'
AND A.SANCTN_STTUS = #{feeRdcxptStat}
)A WHERE ROWNUM BETWEEN #{sNum} AND #{eNum}

mapper.sql:
WHERE A.DELETE_AT = 'N'




		AND ISSU_TRGTER_IHIDNUM = CONCAT(#{jumin1} , '-', #{jumin2})
		AND APPLCNT_IHIDNUM = CONCAT(#{jumin1} , '-', #{jumin2})
		AND APPLCNT_NM = #{issuTrgterNm}
		AND ISSU_TRGTER_NM = #{issuTrgterNm}


		AND A.RGST_ID = #{rgstId}



	AND B.ISSU_DE IS NOT NULL


	AND APPLCNT_NM LIKE CONCAT('%', #{applcntNm}, '%')


AND B.ISSU_NO IN (SELECT MAX(ISSU_NO) FROM CRTF_ISSU WHERE SANCTN_ID IS NOT NULL GROUP BY REQST_NO)


AND B.ISSU_NO IN (SELECT MAX(ISSU_NO) FROM CRTF_ISSU WHERE ISSU_NO = #{issuNo} AND SANCTN_ID IS NOT NULL GROUP BY REQST_NO)



	AND (APPLCNT_IHIDNUM = CONCAT(#{jumin1} , '-', #{jumin2})
		OR ISSU_TRGTER_IHIDNUM = CONCAT(#{jumin1} , '-', #{jumin2}))



	AND (APPLCNT_NM LIKE CONCAT('%', #{issuTrgterNm}, '%') OR 
		ISSU_TRGTER_NM LIKE CONCAT('%', #{issuTrgterNm}, '%')
	)


	AND CONVERT(VARCHAR(10), ISSU_DE, 23) >= #{bgnIssuOn}


	AND CONVERT(VARCHAR(10), ISSU_DE, 23) <= #{endIssuOn}


	AND A.GRAD = #{grad}


	AND A.JSSFC_NO = #{jssfcNo}


	AND A.CHRG_BRFFC = #{chrgBrffc}


	AND A.SPT_SE = #{sptSe}

mapper.select:
SELECT A.* FROM( SELECT ROW_NUMBER() OVER(ORDER BY MAX(A.REQST_NO) DESC) AS ROWNUM , (SELECT CD_NM FROM CMMN_CD WHERE CD_ID = MAX(A.SPT_SE)) SPT_SE_NM, MAX(A.REQST_NO) REQST_NO, MAX(APPLCNT_NM) APPLCNT_NM, ISNULL(CONVERT(VARCHAR(10), MAX(A.RQSTDT), 23), '') RQSTDT, ISNULL(CONVERT(VARCHAR(10), MAX(ISSU_ON), 23), '') ISSU_ON, MAX(ISSU_TRGTER_IHIDNUM) ISSU_TRGTER_IHIDNUM, MAX(ISSU_TRGTER_NM) ISSU_TRGTER_NM, MAX(RECPT_MTH) RECPT_MTH, (SELECT CD_NM FROM CMMN_CD WHERE CD_ID = MAX(A.RECPT_MTH)) RECPT_MTH_NM, MAX(SETLE_MTH) SETLE_MTH, (SELECT CD_NM FROM CMMN_CD WHERE CD_ID = MAX(A.SETLE_MTH)) SETLE_MTH_NM, MAX(JSSFC_NO) JSSFC_NO, MAX(ISSU_CO) ISSU_CO, MAX(A.RGST_ID) RGST_ID, MAX(DETAIL_HIST_INCLS_AT) DETAIL_HIST_INCLS_AT, MAX(IHIDNUM_OTHBC_AT) IHIDNUM_OTHBC_AT, MAX(FEE_RDCXPT_REQST) FEE_RDCXPT_REQST, Max(P.SANCTN_STTUS) SANCTN_STTUS -- 결재상태
FROM CRTF_REQST A, SANCTN_MAIN M, SANCTN_PROGRS P WHERE A.REQST_NO = M.DOC_NO AND M.SANCTN_NO = P.SANCTN_NO AND FEE_RDCXPT_REQST='Y' --수수료감면 신청 AND P.SANCTN_KND = 'ARCS0002' --결재코드 : 수수료감면 신청 AND DELETE_AT = 'N'
AND ( APPLCNT_IHIDNUM LIKE CONCAT(#{jumin1} , '%') OR APPLCNT_IHIDNUM LIKE CONCAT(#{jumin1} , '%') )
AND ( APPLCNT_IHIDNUM LIKE CONCAT('%', #{jumin2}) OR APPLCNT_IHIDNUM LIKE CONCAT('%', #{jumin2}) )
AND APPLCNT_NM LIKE CONCAT('%', #{applcntNm}, '%')
AND ISSU_TRGTER_NM LIKE CONCAT('%', #{issuTrgterNm}, '%')
AND CONVERT(VARCHAR(10), RQSTDT, 23) >= #{bgnRqstdt}
AND CONVERT(VARCHAR(10), RQSTDT, 23) <= #{endRqstdt}
AND CONVERT(VARCHAR(10), ISSU_ON, 23) >= #{bgnIssuOn}
AND CONVERT(VARCHAR(10), ISSU_ON, 23) <= #{endIssuOn}
AND SETLE_MTH = #{setleMth}
AND A.REQST_NO = #{reqstNo}
AND A.REQST_NO IN (SELECT DISTINCT REQST_NO FROM CRTF_ISSU WHERE ISSU_DE IS NULL)
AND A.REQST_NO IN (SELECT DISTINCT REQST_NO FROM CRTF_ISSU WHERE ISSU_DE IS NOT NULL)
AND (SELECT DDC_ASTC_CD FROM EPDBS_LINK.SSO.DBO.V_USER WHERE USER_ID = B.SANCTN_ID) = #{chrgBrffc}
AND P.SANCTN_STTUS = #{feeRdcxptStat}
GROUP BY P.SANCTN_NO ) A WHERE ROWNUM BETWEEN #{sNum} AND #{eNum}

SELECT COUNT(A.SANCTN_NO) 
FROM (
	SELECT 
		P.SANCTN_NO
	FROM CRTF_REQST A, SANCTN_MAIN M, SANCTN_PROGRS P
	WHERE A.REQST_NO = M.DOC_NO 
	AND M.SANCTN_NO = P.SANCTN_NO 
	AND FEE_RDCXPT_REQST='Y'	--수수료감면 신청
	AND P.SANCTN_KND = 'ARCS0002'	--결재코드 : 수수료감면 신청
	AND DELETE_AT = 'N'

		AND (
			APPLCNT_IHIDNUM LIKE CONCAT(#{jumin1} , '%')
			OR APPLCNT_IHIDNUM LIKE CONCAT(#{jumin1} , '%')
		)


		AND (
			APPLCNT_IHIDNUM LIKE CONCAT('%', #{jumin2})
			OR APPLCNT_IHIDNUM LIKE CONCAT('%', #{jumin2})
		)


		AND APPLCNT_NM LIKE CONCAT('%', #{applcntNm}, '%')


		AND ISSU_TRGTER_NM LIKE CONCAT('%', #{issuTrgterNm}, '%')


		AND CONVERT(VARCHAR(10), RQSTDT, 23) >= #{bgnRqstdt}


		AND CONVERT(VARCHAR(10), RQSTDT, 23) <= #{endRqstdt}


		AND CONVERT(VARCHAR(10), ISSU_ON, 23) >= #{bgnIssuOn}


		AND CONVERT(VARCHAR(10), ISSU_ON, 23) <= #{endIssuOn}


		AND SETLE_MTH = #{setleMth}


		AND A.REQST_NO = #{reqstNo}


		AND A.REQST_NO IN (SELECT DISTINCT REQST_NO FROM CRTF_ISSU WHERE ISSU_DE IS NULL)


		AND A.REQST_NO IN (SELECT DISTINCT REQST_NO FROM CRTF_ISSU WHERE ISSU_DE IS NOT NULL)


		AND (SELECT DDC_ASTC_CD FROM EPDBS_LINK.SSO.DBO.V_USER WHERE USER_ID = B.SANCTN_ID) = #{chrgBrffc}


	 	AND P.SANCTN_STTUS = #{feeRdcxptStat}


	GROUP BY P.SANCTN_NO
	)A

mapper.insert:

INSERT INTO CRTF_REQST_PREVIEW(
	APPLCNT_NM, APPLCNT_IHIDNUM, APPLCNT_MOBLPHON, ISSU_TRGTER_IHIDNUM, ISSU_TRGTER_NM,
	ISSU_TRGTER_MOBLPHON, JSSFC_NO, JSSFC_NM, DETAIL_HIST_INCLS_AT, RQSTDT, IHIDNUM_OTHBC_AT, ISSU_CO
) VALUES (
	#{applcntNm}, #{applcntIhidnum}, #{applcntMoblphon}, #{issuTrgterIhidnum}, #{issuTrgterNm}, 
	#{issuTrgterMoblphon}, #{jssfcNoSub}, #{jssfcNm}, #{detailHistInclsAt}, GETDATE(), #{ihidnumOthbcAt}, #{issuCo}
)

	SELECT ISNULL(MAX(REQST_NO), 0)
	FROM CRTF_REQST_PREVIEW

mapper.select:
SELECT COUNT(1)
FROM FEE_CRTF_PROGRS
WHERE REQST_NO = #{reqstNo}

mapper.update:

	SELECT COUNT(1)+1 RCEPT_NO
	FROM CRTF_REQST 
	WHERE RCEPT_NO LIKE CONVERT(VARCHAR(2), GETDATE(), 2) + '-4' + RIGHT(CONVERT(VARCHAR(6), GETDATE(), 12), 4) + '%'


UPDATE CRTF_REQST
SET RCEPT_NO = CONVERT(VARCHAR(2), GETDATE(), 2) + '-4' + RIGHT(CONVERT(VARCHAR(6), GETDATE(), 12), 4) + '-' + REPLICATE(0, 4 - LEN(#{rceptNo})) + #{rceptNo}
	,CHRG_BRFFC = #{chrgBrffc}
WHERE REQST_NO = #{reqstNo}
