mapper.insert:

	SELECT ISNULL(MAX(SN), 0) +1 SN FROM BBS

INSERT INTO BBS(
	SN, SE, CL, SJ, CN, NOTICE_AT, ANSWER, DISPLAY_AT, NTCE_BGNDE, NTCE_ENDDE, RGST_DT, RGST_ID, 
	RDCNT, DWLD_CO, SNDNG_AT, SMS_RECPTN_AGRE_AT, NM, MBTLNUM, CALL_NUMBER, EMAIL, RGST_NM
)VALUES(
	#{sn}, #{se}, #{cl}, #{sj}, #{cn}, #{noticeAt}, #{answer}, #{displayAt}, #{ntceBgnde}, #{ntceEndde}, GETDATE(), #{rgstId},
	0, 0, 'N', #{smsRecptnAgreAt}, #{nm}, #{mbtlnum}, #{callNumber}, #{email}, #{rgstNm}
)

mapper.update:
UPDATE BBS
SET SJ = #{sj}
	,CN = #{cn}
	,CL = #{cl}
	,NOTICE_AT = #{noticeAt}
	,DISPLAY_AT = #{displayAt}
	,NTCE_BGNDE = #{ntceBgnde}
	,NTCE_ENDDE = #{ntceEndde}
	,CHG_DT = GETDATE()
	,CHG_ID = #{chgId}
WHERE SN = #{sn}

	AND RGST_ID = #{rgstId}

UPDATE BBS
SET RDCNT = RDCNT+1
WHERE SN = #{sn}

UPDATE BBS
SET DWLD_CO = DWLD_CO+1
WHERE SN = #{sn}

UPDATE BBS
SET SNDNG_AT = #{sndngAt}
WHERE SN = #{sn}

UPDATE BBS
SET ANSWER = #{answer},
	ANSWER_ID = #{answerId},
	ANSWER_DT = GETDATE()
WHERE SN = #{sn}
AND (
	ANSWER_ID = #{answerId}
	OR ANSWER_ID IS NULL
)

mapper.delete:
DELETE FROM BBS
WHERE SN IN (

	#{seq}

)

	AND RGST_ID = #{rgstId}

mapper.select:
WITH TMP AS ( SELECT ROWNUM FROM( SELECT ROW_NUMBER() OVER(ORDER BY NOTICE_AT DESC, SN DESC) AS ROWNUM, SN FROM BBS A WHERE 1 = 1
) A WHERE A.SN = #{sn} ) SELECT A.*, (CASE WHEN A.ROWNUM > B.ROWNUM THEN 'D' ELSE 'U' END) SE FROM ( SELECT ROW_NUMBER() OVER(ORDER BY NOTICE_AT DESC, SN DESC) AS ROWNUM, SN, SJ, RGST_DT FROM BBS A WHERE 1 = 1
) A, TMP B WHERE A.ROWNUM = B.ROWNUM+1 OR A.ROWNUM = B.ROWNUM-1
